import { createStore } from "redux";
import { composeWithDevTools } from "redux-devtools-extension";

export const BOOK_LIST = "BOOK_LIST";
export const BOOK_DETAILS = "BOOK_DETAILS";

export const getBooksAction = (books) => ({
  type: BOOK_LIST,
  payload: books,
});
export const getBookDetailAction = (bookIndex) => ({
  type: BOOK_DETAILS,
  payload: bookIndex
});

const initialState = {
  bookList: [],
  bookDetail: {},
};


//rootReducer = combineReducers({})
function bookReducer(state = initialState, action) {
  switch (action.type) {
    case BOOK_LIST: {
      return { ...state, bookList: action.payload };
    }
    default:
      return state;
  }
}

function bookItemReducer(state = initialState, action) {
  switch (action.type) {
    case BOOK_DETAILS: {
      const bookIndex = action.payload;
      const book = state.bookList[bookIndex];
      return { ...state, bookDetail: book };
    }
    default:
      return state;
  }
}

export const store = createStore(
  bookReducer,
  bookItemReducer,
  composeWithDevTools()
  // other store enhancers if any
);
