import client from "./client";
import { getBooksAction, toggleLoading } from "../store/reducers/booksReducer";
import { getBookDetailAction } from "../store/reducers/bookDetailReducer";

export const getBooks = () => {
  return (dispatch) => {
    client.get("/books").then((response) => {
      dispatch(getBooksAction(response.data));
    });
  };
};

export const getDetails = (id) => {
  return (dispatch) => {
    dispatch(toggleLoading());
    client.get(`/books/${id}`).then((response) => {
      dispatch(getBookDetailAction(response.data));
    });
  };
};
