import React from "react";
import { getDetails } from "../../../api/books";
import { Spinner, CardBody, CardTitle, CardSubtitle, Card, CardText, Button } from "reactstrap";
import "./styles.scss";
import { useParams } from "react-router";
import { connect } from "react-redux";

class BookDetails extends React.Component {
  state = {
    bookList: [],
    loading: true,
  };
  componentDidMount() {
    this.props.getBookData();
  }

  render() {
    const { bookList, loading } = this.state;

    return (
      <>
        {loading && <Spinner>Loading...</Spinner>}
        <Card className="detail">
    <CardBody>
      <CardTitle tag="h5">
        {bookList.title}
      </CardTitle>
      <CardSubtitle
        className="mb-2 text-muted"
        tag="h6"
      >
        {bookList.description}
      </CardSubtitle>
      <CardText>
        {bookList.excerpt}
      </CardText>
    </CardBody>
  </Card>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  data:state.book.book,
  loading: state.book.isLoading,
});

const mapDispatchToProps = (dispatch) => {
  return {
    getBookData: () => {
      dispatch(getDetails());
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BookDetails);
