import React from "react";
import { getDetails } from "../../../api/books";

class BookDetails extends React.Component {
    state={
        bookList: [],
        loading: true,
    }
    componentDidMount() {
        getDetails(this.props.match.params.id).then((response) => {
            this.setState({
                bookList: response.data,
                loading: false,
            })
        });
        
      }

render() {
    return (
        <>
        {this.state.loading &&
        <div>
        <div>Title: {this.state.bookList.title}</div>
        <div>Description: {this.state.bookList.description} </div>
        <div>Excerpt: {this.state.bookList.excerpt} </div>
        </div>
        }
        </>
    )
}}

export default BookDetails;
