import React, { Component, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { CardGroup, Pagination, Spinner } from "reactstrap";
import Book from "../BookItem/bookItem";
import "./styles.scss";
import "bootstrap/dist/css/bootstrap.min.css";
import Paginations from "./Pagination";

const Books = ({ bookList, loading, page, handleRight, handleLeft }) => {
const [currentPage, setCurrentPage] = useState(1);
const [booksPerPage] = useState(10);
 
const lastBookIndex = currentPage * booksPerPage
const firstBookIndex = lastBookIndex - booksPerPage
const currentBook = bookList.slice(firstBookIndex, lastBookIndex)

  return (
    <>
      <div>Books List</div>
      <div>
        <div>
          <div>
            <CardGroup>
              {!loading &&
                bookList.map((book, index) => {
                  if (index < 10) {
                    console.log(index);
                    return (
                      <Book
                        book={book}
                      />
                    );
                  }
                })}
            </CardGroup>
          </div>
          <Paginations />
          {loading && <Spinner>Loading...</Spinner>}
        </div>
      </div>
    </>
  );
};

export default Books;
