import React from "react";
import { getDetails } from "../../../api/books";
import { Spinner, CardBody, CardTitle, CardSubtitle, Card, CardText, Button } from "reactstrap";

class BookDetails extends React.Component {
  state = {
    bookList: [],
    loading: true,
  };
  componentDidMount() {
    getDetails(this.props.match.params.id).then((response) => {
      this.setState({
        bookList: response.data,
        loading: false,
      });
    });
  }

  render() {
    return (
      <>
        {this.state.loading && <Spinner>Loading...</Spinner>}
        <Card
  >
    <CardBody>
      <CardTitle tag="h5">
        {this.state.bookList.title}
      </CardTitle>
      <CardSubtitle
        className="mb-2 text-muted"
        tag="h6"
      >
        {this.state.bookList.description}
      </CardSubtitle>
      <CardText>
        {this.state.bookList.excerpt}
      </CardText>
      <Button>
        Button
      </Button>
    </CardBody>
  </Card>
      </>
    );
  }
}

export default BookDetails;
