const initialState = {
    bookData: {},
    isLoading: true,
  };
  
  export const getBookDetailAction = (payload) => ({ type: GET_BOOK_BY_ID, payload });
  
  const GET_BOOK_BY_ID = "GET_BOOK_BY_ID";
  
  const bookReducer = (state = initialState, action) => {
    switch (action.type) {
      case GET_BOOK_BY_ID:
        return { isLoading: false, bookData: [...action.payload] };
      default:
        return state;
    }
  };
  
  export default bookReducer;
