import React,{ useEffect, useState } from "react";
import { getBooks } from "./api/books";
import Books from "./pages/Books/BookList/bookList";
import "./styles.scss";
import Paginations from "./pages/Books/BookList/Pagination";

const APIComponentHook = () => {
    const [ bookList, setBookList ] = useState([]);
    const [ loading, setLoading ] = useState(true);
    const [ error, setError ] = useState(false);

    useEffect(() => {
        getBooks()
        .then(({data}) => {
            setBookList(data);
            setLoading(false);
        })
        .catch(() => setError(true));
    },[])

    const [currentPage, setCurrentPage] = useState(1);
    const [booksPerPage] = useState(15);
 
    const lastBookIndex = currentPage * booksPerPage
    const firstBookIndex = lastBookIndex - booksPerPage
    const currentBook = bookList.slice(firstBookIndex, lastBookIndex)

    const paginate = pageNumber => {
        setCurrentPage(pageNumber)
    }

    return (
        <div className="center">
            <Books
            bookList={currentBook}
            loading={loading}
            />
            <Paginations 
            booksPerPage={booksPerPage}
            totalBooks={bookList.length}
            paginate={paginate}
            currentPage={currentPage}
            />
        </div>
      );
}

export default APIComponentHook;
