import React, { Component } from "react";
import Note from "./Note";
import { connect } from "react-redux";
import { Row, Button, Empty } from "antd";
import ModalAdd from "../../components/Modals/modalCreate";
import ModalDelete from "../../components/Modals/modalDelete";
import ModalEdit from "../../components/Modals/modalEdit";
import {
  createModalNoteShowAction,
  deleteModalNoteShowAction,
  editModalNoteShowAction,
  modalNoteCloseAction,
} from "../../store/reducers/modalReducer";
import { createNote, deleteNoteById, editNote, getNoteForEdit, getNoteList } from "../../store/notesThunk";

class NoteList extends Component {
  componentDidMount() {
    this.props.getData();
  }

  handleSubmit = (editValues) => {
    const note = {
      name: editValues.name,
      author: editValues.author,
      description: editValues.description,
    };
    this.props.handleSubmitEdit(note, editValues.uuid);
  }

  render() {
    const { type, loading, notes, noteEdit, id } = this.props;
    return (
      <>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
          <h1>Notes</h1>
          <Button type="primary" onClick={() => this.props.showModalAdd()}>
            Add New Note
          </Button>
        </div>
        <div className="site-card-wrapper">
          <Row gutter={16} style={{ justifyContent: "space-around" }}>
            {!notes.length && <Empty />}
            {!loading &&
              notes.map((note, index) => {
                return (
                  <Note
                    key={index}
                    note={note}
                    showModalDelete={this.props.showModalDelete}
                    showModalEdit={this.props.showModalEdit}
                  />
                );
              })}
          </Row>
          {/* {loading && <Spinner>Loading...</Spinner>} */}
          {type === "create" && (
            <ModalAdd
              visible={true}
              closeModal={this.props.closeModal}
              handleSubmitCreate={this.props.handleSubmitCreate}
            />
          )}
          {type === "delete" && (
            <ModalDelete
              id={id}
              visible={true}
              handleDelete={this.props.handleDelete}
              closeModal={this.props.closeModal}
            />
          )}
          {type === "edit" && (
            <ModalEdit
              visible={true}
              closeModal={this.props.closeModal}
              noteEdit={noteEdit}
              handleSubmitEdit={this.handleSubmit}
            />
          )}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  type: state.modal.type,
  notes: state.notes.noteList,
  loading: state.notes.isLoading,
  noteEdit: state.modal.data,
  id: state.modal.data.id,
});

const mapDispatchToProps = (dispatch) => {
  return {
    getData: () => {
      dispatch(getNoteList());
    },
    showModalAdd: () => {
      dispatch(createModalNoteShowAction());
    },
    handleDelete: (id) => {
      dispatch(deleteNoteById(id));
    },
    closeModal: () => {
      dispatch(modalNoteCloseAction());
    },
    showModalDelete: (id) => {
      dispatch(deleteModalNoteShowAction(id));
    },
    showModalEdit: (id) => {
      dispatch(getNoteForEdit(id));
    },
    handleSubmitEdit: (note, id) => {
      dispatch(editNote(note, id));
    },
    handleSubmitCreate: (values) => {
      dispatch(createNote(values));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(NoteList);
