import { createStore, combineReducers, applyMiddleware } from "redux";
import { composeWithDevTools } from "redux-devtools-extension";
import thunk from "redux-thunk";
import modalCreateReducer from "./reducers/modalCreateReducer";
import modalDeleteReducer from "./reducers/modalDeleteReducer";
import modalEditReducer from "./reducers/modalEditReducer";
import noteDetailsReducer from "./reducers/noteDetailsReducer";
import notesReducer from "./reducers/notesReducer";

const rootReducer = combineReducers({
  notes: notesReducer,
  modalCreate: modalCreateReducer,
  modalDelete: modalDeleteReducer,
  modalEdit: modalEditReducer,
  noteDetails: noteDetailsReducer,
});

const store = createStore(
  rootReducer,
  composeWithDevTools(applyMiddleware(thunk))
);
export default store;
