import React from "react";
import { Form, Field } from "react-final-form";
import { useDispatch } from "react-redux";
import { postNote } from "../../api/notes";
import { EditInputField } from "./EditInputField";
import { EditTextAreaField } from "./EditTextAreaField";
import { InputField } from "./InputField";
import { TextAreaField } from "./TextAreaField";

const EditNoteForm = (params) => {
  const handleSubmit = (event) => {
    dispatch(postNote(event));
    console.log(params.noteEdit);
  };

  const dispatch = useDispatch();

  return (
    <>
      <Form
        id="form"
        onSubmit={handleSubmit}
        initialValues={params.noteEdit}
        render={({ handleSubmit, form, submitting, pristine }) => (
          <form onSubmit={handleSubmit} id="form" initialValues={params.noteEdit}>
            <Field
              addonBefore={<label>Name</label>}
              name="name"
            //   value={note.name}
            //   value="name"
              component={EditInputField}
            />
            <Field
              addonBefore={<label>Author</label>}
              name="author"
            //   value={note.author}
              component={EditInputField}
            />
            <Field
              addonBefore={<label>Description</label>}
              name="description"
              component={EditTextAreaField}
            />
          </form>
        )}
      />
    </>
  );
};

export default EditNoteForm;
