import React, { Component } from "react";
import { deleteNote, getNotes } from "../../api/notes";
import Note from "./Note";
import { connect } from "react-redux";
import { Row, Button } from "antd";
import ModalAdd from "../../components/Modals/modalCreate";
import {
  postNoteCloseAction,
  postNoteShowAction,
} from "../../store/reducers/modalCreateReducer";
import ModalDelete from "../../components/Modals/modalDelete";
import { deleteNoteCloseAction, deleteNoteShowAction } from "../../store/reducers/modalDeleteReducer";

class NoteList extends Component {
  componentDidMount() {
    this.props.getData();
  }

  render() {
    const { loading, notes, visibleAdd, visibleDelete, id } = this.props;
    return (
      <>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
          <h1>Notes</h1>
          <Button type="primary" onClick={() => this.props.showModalAdd()}>
            Add New Note
          </Button>
        </div>
        <div className="site-card-wrapper">
          {visibleAdd && (
            <ModalAdd
              visible={visibleAdd}
              closeModal={this.props.closeModalAdd}
            />
          )}
          {visibleDelete && (
            <ModalDelete
              id={id}
              visible={visibleDelete}
              handleDelete={this.props.handleDelete}
              closeModal={this.props.closeModalDelete}
            />
          )}
          <Row gutter={16} style={{ justifyContent: "space-around" }}>
            {!loading &&
              notes.map((note, index) => {
                return <Note note={note} />;
              })}
          </Row>
          {/* {loading && <Spinner>Loading...</Spinner>} */}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  notes: state.notes.noteList,
  loading: state.notes.isLoading,
  visibleAdd: state.modalCreate.visible,
  visibleDelete: state.modalDelete.visible,
  id: state.modalDelete.id,
});

const mapDispatchToProps = (dispatch) => {
  return {
    getData: () => {
      dispatch(getNotes());
    },
    showModalAdd: () => {
      dispatch(postNoteShowAction());
    },
    handleDelete: (id) => {
      dispatch(deleteNote(id));
    },
    closeModalDelete: () => {
      dispatch(deleteNoteCloseAction());
    },
    closeModalAdd: () => {
      dispatch(postNoteCloseAction());
    },
    showModalDelete: (id) => {
      dispatch(deleteNoteShowAction(id));
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(NoteList);
