export const getNotes = () => {
  return (dispatch) => {
    client.get("/notes").then((response) => {
      dispatch(getNotesAction(response.data));
      // console.log(response);
    });
  };
};

export const postNote = (note) => {
  return (dispatch) => {
    dispatch(postNoteProgressAction());
    client.post("/notes", note).then(() => {
      dispatch(postNoteSuccessAction());
      dispatch(getNotes());
      // console.log(response);
    });
  };
};

export const deleteNote = (id) => {
  return (dispatch) => {
    dispatch(deleteNoteProgressAction());
    client.delete(`/notes/${id}`).then(() => {
      dispatch(deleteNoteSuccessAction());
      dispatch(getNotes());
    });
  };
};

export const getNoteById = (id) => {
  return (dispatch) => {
    dispatch(editNoteProgressAction());
    client.get(`/notes/${id}`).then((response) => {
      dispatch(editNoteSuccessAction(response.data));
      console.log(response);
    })
  }
}
