import React, { Component } from "react";
import {
  deleteNote,
  getNoteById,
  getNoteEdit,
  getNotes,
  postNote,
  putNote,
} from "../../api/notes";
import Note from "./Note";
import { connect } from "react-redux";
import { Row, Button, Empty } from "antd";
import ModalAdd from "../../components/Modals/modalCreate";
import {
  postNoteCloseAction,
  postNoteShowAction,
} from "../../store/reducers/modalCreateReducer";
import ModalDelete from "../../components/Modals/modalDelete";
import {
  deleteNoteCloseAction,
  deleteNoteShowAction,
} from "../../store/reducers/modalDeleteReducer";
import ModalEdit from "../../components/Modals/modalEdit";
import {
  editNoteCloseAction,
  editNoteShowAction,
} from "../../store/reducers/modalEditReducer";

class NoteList extends Component {
  componentDidMount() {
    this.props.getData();
  }

  render() {
    const {
      loading,
      notes,
      visibleAdd,
      visibleDelete,
      id,
      visibleEdit,
      noteEdit,
    } = this.props;
    return (
      <>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
          <h1>Notes</h1>
          <Button type="primary" onClick={() => this.props.showModalAdd()}>
            Add New Note
          </Button>
        </div>
        <div className="site-card-wrapper">
          {visibleAdd && (
            <ModalAdd
              visible={visibleAdd}
              closeModal={this.props.closeModalAdd}
              handleSubmitCreate={this.props.handleSubmitCreate}
            />
          )}
          {visibleDelete && (
            <ModalDelete
              id={id}
              visible={visibleDelete}
              handleDelete={this.props.handleDelete}
              closeModal={this.props.closeModalDelete}
            />
          )}
          {visibleEdit && (
            <ModalEdit
              visible={visibleEdit}
              closeModal={this.props.closeModalEdit}
              noteEdit={noteEdit}
              handleSubmitEdit={this.props.handleSubmitEdit}
            />
          )}
          <Row gutter={16} style={{ justifyContent: "space-around" }}>
            {!notes.length && <Empty />}
            {!loading &&
              notes.map((note, index) => {
                return (
                  <Note
                    key={index}
                    note={note}
                    showModalDelete={this.props.showModalDelete}
                    showModalEdit={this.props.showModalEdit}
                    // getNoteById={this.props.getNoteById}
                  />
                );
              })}
          </Row>
          {/* {loading && <Spinner>Loading...</Spinner>} */}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  notes: state.notes.noteList,
  loading: state.notes.isLoading,
  visibleAdd: state.modalCreate.visible,
  visibleDelete: state.modalDelete.visible,
  id: state.modalDelete.id,
  visibleEdit: state.modalEdit.visible,
  noteEdit: state.modalEdit.note,
});

const mapDispatchToProps = (dispatch) => {
  return {
    getData: () => {
      dispatch(getNotes());
    },
    showModalAdd: () => {
      dispatch(postNoteShowAction());
    },
    handleDelete: (id) => {
      dispatch(deleteNote(id));
    },
    closeModalDelete: () => {
      dispatch(deleteNoteCloseAction());
    },
    closeModalAdd: () => {
      dispatch(postNoteCloseAction());
    },
    showModalDelete: (id) => {
      dispatch(deleteNoteShowAction(id));
    },
    showModalEdit: (id) => {
      dispatch(editNoteShowAction(id));
      dispatch(getNoteEdit(id));
    },
    closeModalEdit: () => {
      dispatch(editNoteCloseAction());
    },
    handleSubmitEdit: (editValues) => {
      const note = {
        name: editValues.name,
        author: editValues.author,
        description: editValues.description,
      };
      dispatch(putNote(note, editValues.uuid));
    },
    handleSubmitCreate: (values) => {
      dispatch(postNote(values));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(NoteList);
