const initialState = {
    noteList: [],
    isLoading: true,
  };
  
  const GET_ALL_NOTES = "GET_ALL_NOTES";
  
  export const getNotesAction = (payload) => ({ type: GET_ALL_NOTES, payload });
  
  const notesReducer = (state = initialState, action) => {
    switch (action.type) {
      case GET_ALL_NOTES:
        return { isLoading: false, noteList: action.payload };
      default:
        return state;
    }
  };
  
  export default notesReducer;
