import { Modal, Button } from "antd";
// import { Form, Field } from "react-final-form";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { deleteNote } from "../../api/notes";
import { postNoteCloseAction } from "../../store/reducers/modalCreateReducer";
import { deleteNoteCloseAction } from "../../store/reducers/modalDeleteReducer";
import CreateNoteForm from "../Form/CreateNoteForm";

const ModalDelete = ({ visible }) => {
  //  const { visible, loading } = this.props;
  
  const dispatch = useDispatch();
  const { id } = useSelector((state) => state.modalDelete);
  const handleDelete = () => {
      dispatch(deleteNote(id));
      console.log(id);
  };

  const closeModal = () => {
    dispatch(deleteNoteCloseAction());
  };

  return (
    <>
      <Modal
        visible={visible}
        title="Title"
        // onOk={handleOk}
        onCancel={closeModal}
        footer={[
          <Button key="back" onClick={closeModal}>
            Return
          </Button>,
          <Button
            key="delete"
            type="primary"
            form="form"
            // loading={loading}
             onClick={() => handleDelete()}
          >
            Submit
          </Button>,
        ]}
      >
      </Modal>
    </>
  );
};

export default ModalDelete;
