import { call } from 'file-loader';
import { put, takeEvery } from 'redux-saga';
import { getNotes } from '../../api/notes';

function* getNotesSaga() {
  try {
    const data = yield call(getNotes);
    yield put({
      type: 'GET_ALL_NOTES', payload: data,
    });
  } catch (error) {
    yield put({
      type: 'CLOSE_ALL_NOTES',
    });
  }
}

function* watcherGetNotesSaga() {
  yield takeEvery('GET_ALL_NOTES_IN_PROGRESS', getNotesSaga);
}

export default watcherGetNotesSaga;
