import { Modal, Button } from "antd";
// import { Form, Field } from "react-final-form";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { postNoteCloseAction } from "../../store/reducers/modalCreateReducer";
import { deleteNoteCloseAction } from "../../store/reducers/modalDeleteReducer";
import CreateNoteForm from "../Form/CreateNoteForm";

const ModalDelete = ({  id }) => {
  //  const { visible, loading } = this.props;
//   const { visible } = useSelector((state) => state.note);

  const dispatch = useDispatch();
  const { visible } = useSelector((state) => state.modalDelete.visible);
  const deleteNote = (id) => {
      dispatch(deleteNote(id));
  }

  const closeModal = () => {
    dispatch(deleteNoteCloseAction());
  };

  return (
    <>
      <Modal
        visible={visible}
        title="Title"
        // onOk={handleOk}
        onCancel={closeModal}
        footer={[
          <Button key="back" onClick={closeModal}>
            Return
          </Button>,
          <button
            key="submit"
            type="primary"
            form="form"
            // loading={loading}
             onClick={() => deleteNote(id)}
          >
            Submit
          </button>,
        ]}
      >
      </Modal>
    </>
  );
};

export default ModalDelete;
