import { Col, Card, Menu, Dropdown } from "antd";
import { DownOutlined } from "@ant-design/icons";
import ModalDelete from "../../components/Modals/modalDelete";
import { useDispatch, useSelector } from "react-redux";
import { deleteNoteShowAction } from "../../store/reducers/modalDeleteReducer";
import { deleteNote } from "../../api/notes";

const Note = (params) => {
  // console.log(params);
  const dispatch = useDispatch();

  const showModal = () => {
    dispatch(deleteNoteShowAction(params.note.uuid));
  };

  // const handleDelete = () => {
  //   dispatch(deleteNote(params.note.uuid));
  // };

  const menu = (
    <Menu>
      <Menu.Item key="0">
        <a href="#">Edit</a>
      </Menu.Item>
      <Menu.Item key="1">
        <a href="#">Details</a>
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item key="3" onClick={() => showModal()}>
        Delete
      </Menu.Item>
    </Menu>
  );

  return (
    <Col span={8}>
      <Card
        title={params.note.name}
        bordered={true}
        style={{ marginBottom: "5px" }}
      >
        <div>{params.note.description}</div>
        <div>
          <Dropdown overlay={menu} trigger={["click"]}>
            <a
              href="#"
              className="ant-dropdown-link"
              onClick={(e) => {
                e.preventDefault();
              }}
            >
              Click me <DownOutlined />
            </a>
          </Dropdown>
        </div>
      </Card>
     
    </Col>
  );
};

export default Note;
