const initialState = {
    // visible: false,
    loadingDetail: false,
    note: {},
  };
  
  const NOTE_DETAIL_IN_PROGRESS = "NOTE_DETAIL_IN_PROGRESS";
  const NOTE_DETAIL_SUCCESS = "NOTE_DETAIL_SUCCESS";
  const NOTE_DETAIL_SHOW = "NOTE_DETAIL_SHOW";
  const NOTE_DETAIL_CLOSE = "NOTE_DETAIL_CLOSE";
  
  export const getNoteByIdShowAction = () => ({
    type: NOTE_DETAIL_SHOW,
  });
  
  export const getNoteByIdCloseAction = () => ({
    type: NOTE_DETAIL_CLOSE,
  });
  
  export const getNoteByIdProgressAction = () => ({
    type: NOTE_DETAIL_IN_PROGRESS,
  });
  
  export const getNoteByIdSuccessAction = (payload) => ({
    type: NOTE_DETAIL_SUCCESS, payload
  });
  
  const noteDetailsReducer = (state = initialState, action) => {
    switch (action.type) {
      case NOTE_DETAIL_SHOW:
        return { ...state, loadingModal: false, visible: true };
      case NOTE_DETAIL_IN_PROGRESS:
        return { ...state, loadingModal: true };
      case NOTE_DETAIL_SUCCESS:
        return { ...state, loadingModal: false, note: action.payload };
      case NOTE_DETAIL_CLOSE:
        return { ...state, loadingModal: false, visible: false };
      default:
        return state;
    }   
  };
  
  export default noteDetailsReducer;
