import { GET_ALL_NOTES } from "../action-types/note.action-types";

const initialState = {
  noteList: [],
  isLoading: true,
};

const notesReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ALL_NOTES:
      return { isLoading: false, noteList: action.payload };
    default:
      return state;
  }
};

export default notesReducer;
