import { Modal, Button } from "antd";
// import { Form, Field } from "react-final-form";
import React from "react";
import { useDispatch } from "react-redux";
import { postNoteCloseAction } from "../../store/reducers/modalCreateReducer";
import CreateNoteForm from "../Form/CreateNoteForm";

const ModalAdd = ({ visible, loading, handleCancel }) => {
  //  const { visible, loading } = this.props;

  const dispatch = useDispatch();

  const closeModal = () => {
    dispatch(postNoteCloseAction());
  };

  return (
    <>
      <Modal
        visible={visible}
        title="Title"
        // onOk={handleOk}
        onCancel={closeModal}
        footer={[
          <Button key="back" onClick={closeModal}>
            Return
          </Button>,
          <Button
            key="submit"
            type="primary"
            htmlType="submit"
            form="form"
            loading={loading}
            // onClick={handleOk}
          >
            Submit
          </Button>,
        ]}
      >
        <CreateNoteForm />
      </Modal>
    </>
  );
};

export default ModalAdd;
