import React from "react";
import { Form, Field } from "react-final-form";
import { useDispatch } from "react-redux";
import { postNote, putNote } from "../../api/notes";
import { EditInputField } from "./EditInputField";
import { EditTextAreaField } from "./EditTextAreaField";

const EditNoteForm = ({ noteEdit }) => {
  const handleSubmit = (formValues) => {
    //   const note = {
    //       name: event.name,
    //       author: event.author,
    //       description: event.description
    //   }
    // dispatch(putNote(event,event.uuid));
    dispatch(putNote(formValues));
    // console.log(event);
  };

  const dispatch = useDispatch();

  return (
    <>
      <Form
        id="form"
        onSubmit={handleSubmit}
         initialValues={noteEdit}
        render={({ handleSubmit, form, submitting, pristine }) => (
          <form onSubmit={handleSubmit} id="form">
            <Field
              addonBefore={<label>Name</label>}
              name="name"
              component={EditInputField}
            />
            <Field
              addonBefore={<label>Author</label>}
              name="author"
              component={EditInputField}
            />
            <Field
              addonBefore={<label>Description</label>}
              name="description"
              component={EditTextAreaField}
            />
          </form>
        )}
      />
    </>
  );
};

export default EditNoteForm;
