import React from "react";
import { Form } from "react-final-form";
import { Input, Button } from "antd";
import { postNote } from "../../api/notes";

const CreateNoteForm = (handleCancel) => {
  const { TextArea } = Input;
  let note = {
    name: "",
    author: "",
    description: "",
  };

  const handleOk = () => {
    postNote(note);
  };

const onSubmit = (e) => {
  e.preventDefault();
}

  const handleChange = (e) => {
    e.target.name === "name"
      ? (note.name = e.target.value)
      : e.target.name === "author"
      ? (note.author = e.target.value)
      : (note.description = e.target.value);
    //   console.log(e.target.value)
  };

  return (
    <>
      <Form onSubmit={onSubmit}>
        <Input name="name" placeholder="Name" onChange={handleChange} />
        <Input name="author" placeholder="Author" onChange={handleChange} />
        <TextArea name="description" rows={4} onChange={handleChange} />

        {/* {console.log(note)} */}
        <Button
          key="submit"
          type="primary"
          // loading={loading}
          onClick={handleOk}
        >
          Submit
        </Button>
      </Form>
    </>
  );
};

export default CreateNoteForm;
