import { Col, Card, Menu, Dropdown } from "antd";
import { DownOutlined } from "@ant-design/icons";

const Note = (params) => {
  // console.log(params);

  const handleDelete = () => {
    console.log(params.note.uuid);
  };

  const menu = (
    <Menu>
      <Menu.Item key="0">
        <a href="#">Edit</a>
      </Menu.Item>
      <Menu.Item key="1">
        <a href="#">Details</a>
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item key="3" onClick={handleDelete}>
        Delete
      </Menu.Item>
    </Menu>
  );

  return (
    <Col span={8}>
      <Card
        title={params.note.name}
        bordered={true}
        style={{ marginBottom: "5px" }}
      >
        <div>{params.note.description}</div>
        <div>
          {" "}
          <Dropdown overlay={menu} trigger={["click"]} id={params.note.uuid}>
            <a
            href="#"
              className="ant-dropdown-link"
              onClick={(e) => e.preventDefault()}
            >
              Click me <DownOutlined />
            </a>
          </Dropdown>
        </div>
      </Card>
    </Col>
  );
};

export default Note;
