import { Alert } from "antd";
import {
  postNoteProgressAction,
  postNoteSuccessAction,
} from "../store/reducers/modalCreateReducer";
import {
  deleteNoteProgressAction,
  deleteNoteSuccessAction,
} from "../store/reducers/modalDeleteReducer";
import {
  editNoteCloseAction,
  editNoteProgressAction,
  editNoteSuccessAction,
} from "../store/reducers/modalEditReducer";
import { getNotesAction } from "../store/reducers/notesReducer";
import client from "./client";
// import { getBooksAction } from "../store/reducers/booksReducer";
