import { Col, Card, Menu, Dropdown } from "antd";
import { DownOutlined } from "@ant-design/icons";
import { NavLink } from "react-router-dom";

const Note = (params) => {
  const menu = (
    <Menu destroyPopupOnHide>
      <Menu.Item
        key="0"
        onClick={() => {
          params.showModalEdit("edit", params.note.uuid);
          params.getNote();
        }}
      >
        Edit
      </Menu.Item>
      <Menu.Item key="1">
        <NavLink to={`/note/${params.note.uuid}`}>Details</NavLink>
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item
        key="3"
        onClick={() => params.showModalDelete("delete", params.note.uuid)}
      >
        Delete
      </Menu.Item>
    </Menu>
  );

  return (
    <Col span={8}>
      <Card
        title={params.note.name}
        bordered={true}
        style={{ marginBottom: "5px" }}
      >
        <div>{params.note.description}</div>
        {/* <div> */}
        <Dropdown overlay={menu} trigger={["click"]} destroyPopupOnHide>
          <a
            href="#/"
            className="ant-dropdown-link"
            onClick={(e) => {
              e.preventDefault();
            }}
          >
            Click me <DownOutlined />
          </a>
        </Dropdown>
        {/* </div> */}
      </Card>
    </Col>
  );
};

export default Note;
