const initialState = {
  visible: false,
  loadingModal: false,
  id: "",
};

const NOTE_DELETE_IN_PROGRESS = "NOTE_DELETE_IN_PROGRESS";
const NOTE_DELETE_SUCCESS = "NOTE_DELETE_SUCCESS";
const NOTE_DELETE_SHOW = "NOTE_DELETE_SHOW";
const NOTE_DELETE_CLOSE = "NOTE_DELETE_CLOSE";

export const deleteNoteShowAction = (payload) => ({
  type: NOTE_DELETE_SHOW,
  payload,
});

export const deleteNoteCloseAction = () => ({
  type: NOTE_DELETE_CLOSE,
});

export const deleteNoteProgressAction = () => ({
  type: NOTE_DELETE_IN_PROGRESS,
});

export const deleteNoteSuccessAction = () => ({
  type: NOTE_DELETE_SUCCESS,
});

const modalDeleteReducer = (state = initialState, action) => {
  switch (action.type) {
    case NOTE_DELETE_SHOW:
      return { loadingModal: false, visible: true, id: action.payload };
    case NOTE_DELETE_IN_PROGRESS:
      return { loadingModal: true };
    case NOTE_DELETE_SUCCESS:
      return { loadingModal: false, visible: false, id: "" };
    case NOTE_DELETE_CLOSE:
      return { loadingModal: false, visible: false, id: "" };
    default:
      return state;
  }
};

export default modalDeleteReducer;
