import { Modal, Button } from "antd";
// import { Form, Field } from "react-final-form";
import React from "react";
import { useDispatch } from "react-redux";
import { postNoteSuccessAction } from "../../store/reducers/modalCreateReducer";
import CreateNoteForm from "../Form/CreateNoteForm";

const ModalAdd = ({ visible, loading, handleCancel }) => {
  //  const { visible, loading } = this.props;

const dispatch = useDispatch;

  return (
    <>
      {/* <Button type="primary" onClick={this.showModal}>
          Open Modal with customized footer
        </Button> */}
      <Modal
        visible={visible}
        title="Title"
        // onOk={handleOk}
        // onCancel={handleCancel}
        footer={[
          <Button key="back" onClick={dispatch(postNoteSuccessAction())}>
            Return
          </Button>,
          <button
          key="submit"
          type="primary"
          form="form"
          // loading={loading}
          // onClick={handleOk}
        >
          Submit
        </button>
        //   <Button
        //     key="submit"
        //     type="primary"
        //     loading={loading}
        //     onClick={handleOk}
        //   >
        //     Submit
        //   </Button>,
        ]}
      >
        <CreateNoteForm />
      </Modal>
    </>
  );
};

export default ModalAdd;
