import { message } from "antd";
import {
  postNoteProgressAction,
  postNoteSuccessAction,
} from "../store/reducers/modalCreateReducer";
import {
  deleteNoteProgressAction,
  deleteNoteSuccessAction,
} from "../store/reducers/modalDeleteReducer";
import {
  editNoteCloseAction,
  editNoteProgressAction,
  editNoteSuccessAction,
} from "../store/reducers/modalEditReducer";
import { getNoteByIdProgressAction, getNoteByIdSuccessAction } from "../store/reducers/noteDetailsReducer";
import { getNotesAction } from "../store/reducers/notesReducer";
import client from "./client";

export const getNotes = () => {
  return (dispatch) => {
    client.get("/notes").then((response) => {
      dispatch(getNotesAction(response.data));
      // console.log(response);
    });
  };
};

export const postNote = (note) => {
  return (dispatch) => {
    dispatch(postNoteProgressAction());
    client.post("/notes", note).then(() => {
      dispatch(postNoteSuccessAction());
      dispatch(getNotes());
      const info = () => {message.info('This is a normal message')};
      info();
      // console.log(response);
    });
  };
};

export const deleteNote = (id) => {
  return (dispatch) => {
    dispatch(deleteNoteProgressAction());
    client.delete(`/notes/${id}`).then(() => {
      dispatch(deleteNoteSuccessAction());
      dispatch(getNotes());
    });
  };
};

export const getNoteEdit = (id) => {
  return (dispatch) => {
    dispatch(editNoteProgressAction());
    client.get(`/notes/${id}`).then((response) => {
      dispatch(editNoteSuccessAction(response));
      // console.log(response);
    });
  };
};

export const putNote = (note, id) => {
  return (dispatch) => {
    console.log(note);
    client.put(`/notes/${id}`, note).then(() => {
      dispatch(editNoteCloseAction());
      dispatch(getNotes());
    });
  };
};

export const getNoteById = (id) => {
  return (dispatch) => {
    dispatch(getNoteByIdProgressAction());
    client.get(`/notes/${id}`).then((response) => {
      dispatch(getNoteByIdSuccessAction(response));
      // console.log(response);
    });
  };
};
