import React, { Component } from "react";
import { getNotes, postNote } from "../../api/notes";
import Note from "./Note";
import { connect } from "react-redux";
import { Row, Button } from "antd";
import ModalAdd from "../../components/Modals/modalCreate";

class NoteList extends Component {
  
  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = () => {
    this.setState({ loadingM: true });
    setTimeout(() => {
      this.setState({ loadingM: false, visible: false });
    }, 3000);
  };

  handleCancel = () => {
    this.setState({ visible: false });
  };

  componentDidMount() {
    this.props.getData();
  }

  render() {
    const { loading, notes, visible, loadingModal } = this.props;
    // console.log(notes);
    return (
      <>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
          <h1>Notes</h1>
          <Button
            type="primary"
            onClick={this.showModal}
          >
            Add New Note
          </Button>
        </div>
        <div className="site-card-wrapper">
        {visible && <ModalAdd
                visible={visible}
                loadingM={loadingModal}
                handleOk={this.handleOk}
                handleCancel={this.handleCancel}

              />}
          <Row gutter={16} style={{ justifyContent: "space-around" }}>
            {!loading &&
              notes.map((note, index) => {
                return <Note note={note} />;
              })}
          </Row>
          {/* {loading && <Spinner>Loading...</Spinner>} */}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  notes: state.notes.noteList,
  loading: state.notes.isLoading,
  visible: state.modalCreate.visible,
  loadingModal: state.modalCreate.loadingModal,
});

const mapDispatchToProps = (dispatch) => {
  return {
    getData: () => {
      dispatch(getNotes());
    },
    postData: () => {
      dispatch(postNote());
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(NoteList);
