import { Col, Card, Menu, Dropdown } from "antd";
import { DownOutlined } from "@ant-design/icons";

const menu = (
  <Menu>
    <Menu.Item key="0">
      <a href=" ">Edit</a>
    </Menu.Item>
    <Menu.Item key="1">
      <a href=" ">Details</a>
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="3">Delete</Menu.Item>
  </Menu>
);

const Note = (params) => {
  // console.log(params);
  return (
    <Col span={8}>
      <Card
        title={params.note.name}
        bordered={true}
        style={{ marginBottom: "5px" }}
      >
        <Dropdown overlay={menu} trigger={["click"]}>
          <a className="ant-dropdown-link" onClick={(e) => e.preventDefault()}>
            Click me <DownOutlined />
          </a>
        </Dropdown>
        {params.note.description}
      </Card>
    </Col>
  );
};

export default Note;
