import { Modal, Button } from "antd";
import React from "react";
import NoteForm from "../Form/NoteForm";
import Proptypes from "prop-types";

const ModalEdit = ({
  visible,
  loading,
  closeModal,
  noteEdit,
  handleSubmitEdit,
  type,
}) => {
  
  return (
    <>
      <Modal
        visible={visible}
        title="Edit"
        // onOk={handleOk}
        onCancel={closeModal}
        footer={[
          <Button key="back" onClick={closeModal}>
            Return
          </Button>,
          <Button
            key="submit"
            type="primary"
            htmlType="submit"
            form="form"
            loading={loading}
            // onClick={handleOk}
          >
            Submit
          </Button>,
        ]}
      >
        <NoteForm handleSubmit={handleSubmitEdit} note={noteEdit} loading={loading} type={type} />
      </Modal>
    </>
  );
};

ModalEdit.propTypes ={
  visible: Proptypes.bool,
  closeModal: Proptypes.func,
  loading: Proptypes.bool,
  noteEdit: Proptypes.object,
  handleSubmitEdit: Proptypes.func,
  type: Proptypes.string,
}

export default ModalEdit;
