const initialState = {
  loading: false,
  data: {},
  type: "",
};

const MODAL_NOTE_IN_PROGRESS = "MODAL_NOTE_IN_PROGRESS";
const NOTE_EDIT_SUCCESS = "NOTE_EDIT_SUCCESS";
const MODAL_NOTE_CLOSE = "MODAL_NOTE_CLOSE";
const MODAL_NOTE_EDIT_SHOW = "MODAL_NOTE_EDIT_SHOW";
const MODAL_NOTE_CREATE_SHOW = "MODAL_NOTE_CREATE_SHOW";
const MODAL_NOTE_DELETE_SHOW = "MODAL_NOTE_DELETE_SHOW";
const MODAL_NOTE_SUCCESS = "MODAL_NOTE_SUCCESS";
const GET_NOTE_FOR_EDIT = "GET_NOTE_FOR_EDIT";

export const editModalNoteShowAction = () => ({
  type: MODAL_NOTE_EDIT_SHOW,
});

export const getNoteForEditAction = (payload) => ({
  type: GET_NOTE_FOR_EDIT,
  payload,
});

export const deleteModalNoteShowAction = (payload) => ({
  type: MODAL_NOTE_DELETE_SHOW,
  payload,
});

export const createModalNoteShowAction = () => ({
  type: MODAL_NOTE_CREATE_SHOW,
});

export const modalNoteCloseAction = () => ({
  type: MODAL_NOTE_CLOSE,
});

export const modalNoteProgressAction = () => ({
  type: MODAL_NOTE_IN_PROGRESS,
});

export const modalNoteSuccessAction = () => ({
  type: MODAL_NOTE_SUCCESS,
});

export const editNoteSuccessAction = (payload) => ({
  type: NOTE_EDIT_SUCCESS,
  payload,
});

const modalReducer = (state = initialState, action) => {
  switch (action.type) {
    case MODAL_NOTE_EDIT_SHOW:
      return {
        ...state,
        loading: false,
        type: "edit",
      };
    case MODAL_NOTE_CREATE_SHOW:
      return {
        ...state,
        loading: false,
        type: "create",
      };
    case MODAL_NOTE_DELETE_SHOW:
      return {
        ...state,
        loading: false,
        type: "delete",
        data: { id: action.payload },
      };
    case MODAL_NOTE_IN_PROGRESS:
      return { ...state, loading: true };
    case MODAL_NOTE_SUCCESS:
      return { ...state, loading: false, data: {}, type: "" };
    case MODAL_NOTE_CLOSE:
      return { ...state, loading: false, data: {}, type: "" };
    case GET_NOTE_FOR_EDIT:
      return { ...state, data: action.payload };
    default:
      return state;
  }
};

export default modalReducer;
