const initialState = {
    visible: false,
    loadingModal: false,
    note: {},
  };
  
  const NOTE_EDIT_IN_PROGRESS = "NOTE_EDIT_IN_PROGRESS";
  const NOTE_EDIT_SUCCESS = "NOTE_EDIT_SUCCESS";
  const NOTE_EDIT_SHOW = "NOTE_EDIT_SHOW";
  const NOTE_EDIT_CLOSE = "NOTE_EDIT_CLOSE";
  
  export const editNoteShowAction = () => ({
    type: NOTE_EDIT_SHOW,
  });
  
  export const editNoteCloseAction = () => ({
    type: NOTE_EDIT_CLOSE,
  });
  
  export const editNoteProgressAction = () => ({
    type: NOTE_EDIT_IN_PROGRESS,
  });
  
  export const editNoteSuccessAction = (payload) => ({
    type: NOTE_EDIT_SUCCESS, payload
  });
  
  const modalEditReducer = (state = initialState, action) => {
    switch (action.type) {
      case NOTE_EDIT_SHOW:
        return { loadingModal: false, visible: true };
      case NOTE_EDIT_IN_PROGRESS:
        return { loadingModal: true };
      case NOTE_EDIT_SUCCESS:
        return { loadingModal: false, visible: false, note: action.payload };
      case NOTE_EDIT_CLOSE:
        return { loadingModal: false, visible: false };
      default:
        return state;
    }   
  };
  
  export default modalEditReducer;
