const initialState = {
  visible: true,
  loadingModal: false,
};

const NOTE_CREATE_IN_PROGRESS = "NOTE_CREATE_IN_PROGRESS";
const NOTE_CREATE_SUCCESS = "NOTE_CREATE_SUCCESS";
const NOTE_CREATE_SHOW = "NOTE_CREATE_SHOW";

export const postNoteShowAction = () => ({
  type: NOTE_CREATE_SHOW,
});

export const postNoteProgressAction = () => ({
  type: NOTE_CREATE_IN_PROGRESS,
});

export const postNoteSuccessAction = () => ({
  type: NOTE_CREATE_SUCCESS,
});

const modalCreateReducer = (state = initialState, action) => {
  switch (action.type) {
    case NOTE_CREATE_SHOW:
      return { loadingModal: false, visible: true };
    case NOTE_CREATE_IN_PROGRESS:
      return { loadingModal: true };
    case NOTE_CREATE_SUCCESS:
      return { loadingModal: false, visible: false };
    default:
      return state;
  }
};

export default modalCreateReducer;
