import React from "react";
import { addTodoAction, removeTodoAction } from "../store/store";

export class TodoList extends React.Component {
  state = {
    todoText: {
      name: "todoText",
      label: "Todo text",
      value: "",
      error: null,
      validator: (value = "") => {
        return value ? false : "Required";
      },
    },
  };

  handleSubmit = (event) => {
    event.preventDefault();
  };

  handleChange = (event) => {
    const { value, name } = event.target;
    const currentField = this.state.fields[name];
    this.setState({
      fields: {
        ...this.state.fields,
        [name]: { ...currentField, value },
      },
    });
    //  console.log(this.state.fields.todoText.value);
  };

  // handleAdding = (event) => {
  //     let todoItem = {
  //         value : ""
  //     }
  //     const { todoList, value } = this.state;
  //     todoItem.value = this.state.fields.todoText.value;
  //     event.preventDefault();
  //     this.setState({
  //         todoList : [...todoList,todoItem],
  //         fields: {
  //             todoText: {
  //                 name: "todoText",
  //                 label: "Todo text",
  //                 value: "",
  //     }}});
  //     console.log(value);
  //    // this.state.fields.todoText.value = "";
  // }

  render() {
    console.log(this.props);
    const { todoText } = this.state;

    return (
      <>
        {/* <div>
                    <div>Todo List</div>
                    {todoList.map((todo) => (
                        <div>{todo.value}</div>
                    ))}
                </div> */}
        <hr style={{ width: "100%" }} />
        <div>
          <div>Todo Add Form</div>
          <form onSubmit={this.handleSubmit}>
            <label style={{ paddingRight: "20px" }} htmlFor={todoText.name}>
              {todoText.label}
            </label>
            <input
              type="text"
              onChange={this.handleChange}
              value={todoText.value}
              name={todoText.name}
              id={todoText.name}
            />
            {todoText.value.length === 0 && <span>Field can't be empty</span>}
            {todoText.value.length > 15 && (
              <span>Field contains too much symbols</span>
            )}
            <button
              disabled={
                todoText.value.length === 0 || todoText.value.length > 15
              }
              style={{ marginLeft: "20px" }}
              onClick={this.addTodo}
            >
              Add todo
            </button>
            {/* disabled={this.state.todoList.length === 0}  || todoList.length === 9  */}
            <button onClick={this.removeTodo}>Remove</button>
          </form>
        </div>
      </>
    );
  }

  mapStateToProps = (state) => {
    return {
      todoList: state.todoList,
    };
  };

  mapDispatchToProps = (dispatch) => {
    return {
      addTodo: () => {
        dispatch(addTodoAction(this.state.todoText));
        console.log(this.state.todoText.value);
      },
      removeTodo: () => {
        dispatch(removeTodoAction());
      },
    };
  };
}

export default TodoList;
