import React from "react";
import { addTodoAction, removeTodoAction } from "../store/store";
import { connect } from "react-redux";

export class TodoList extends React.Component {
  state = {
    todoText: "",
  };

  render() {
    console.log(this.state.fields.todoText.value);
    const { todoText } = this.state;

    return (
      <>
        <hr style={{ width: "100%" }} />
        <div>
          <div>Todo Add Form</div>
          <form onSubmit={e => {
          e.preventDefault();
          const { todoText } = this.state;
          const { addTodo } = this.props;
          addTodo({ text: todoText });
        }}>
            <input
              type="text"
              onChange={(event) => this.setState({ todoText: event.target.value })}
              value={todoText}
            />
            {todoText.length === 0 && <span>Field can't be empty</span>}
            {todoText.length > 15 && (
              <span>Field contains too much symbols</span>
            )}
            <button
              disabled={
                todoText.length === 0 || todoText.length > 15
              }
              type="submit"
            >
              Add todo
            </button>
          </form>
          {this.props.todos.map((todo, index) => <div>
          <span>{todo.text}</span>
          <span onClick={() => {this.props.removeTodo(index)}}>‚ùå</span>
        </div>)}
        </div>
      </>
    );
  }

  mapStateToProps = (state) => {
    return {
      todoList: state.todoList,
    };
  };

  mapDispatchToProps = (dispatch) => {
    return {
      addTodo: () => {
        dispatch(addTodoAction(this.state.fields.todoText));
        console.log(this.state.fields.todoText.value);
      },
      removeTodo: () => {
        dispatch(removeTodoAction());
      },
    };
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(TodoList);
